import React, { Component } from 'react';
import Todos from './components/Todos';


import './App.css';

class App extends Component {


  state = {
    todos: [
      {
        id: 0,
        title: 'Take out the trash',
        checked: false
      },
      {
        id: 1,
        title: 'Yankee with no brim',
        checked: false
      },
      {
        id: 2,
        title: 'Based on what?',
        checked: false
      }
    ]
	}

	toggleCheck = (id) => {
		console.log(id)
		this.setState({todos: this.state.todos.map(todo => {
			if (todo.id === id) {
				todo.checked = !todo.checked
			}
			return todo
		})})
	}


  render() {
    return (
        <div className='App'>
            <Todos todos={this.state.todos} toggleCheck={this.toggleCheck} />
        </div>
    );
  }
}

export default App;
